mapper.insert:

	insert into member (no,name,email,password,gender,join_date)
	values(default,#{name},#{email},#{password},cast(#{gender} as enum_member_gender),now())


	
		 SELECT currval('member_no_seq');

mapper.select:

	select no,name 
	from member 
	where email=#{email} and password=#{password}


	select no,name,email,password,gender 
	from member 
	where no = #{no}


	select no,name,email,password,gender 
	from member
	where email = #{email}

mapper.update:
update member set name=#{name},
password=#{password},
gender= cast(#{gender} as enum_member_gender) where no = #{no}
